    private void btnSairTelaPrincipal() {
        Object[] options = {"Sim", "Não"};
        int btnSair = JOptionPane.showOptionDialog(null, "Deseja realmente sair?", "Aviso",
                JOptionPane.DEFAULT_OPTION, JOptionPane.QUESTION_MESSAGE,
                null, options, options[0]);
        if (btnSair == 0) {
            System.exit(0);
        }
    }

    private void menuSobre() {
        Sobre sobre = new Sobre();
        sobre.setVisible(true);
    }

    private void insereData() {
        Date data = new Date();
        DateFormat formata = DateFormat.getDateInstance(DateFormat.SHORT);
        //lblData.setText(data.toString()); //troca a "label Data" pela funcao date
        lblData.setText(formata.format(data)); //troca a "label Data" pela funcao date e formata
    }   
    
    private void menCadUserFull() {
        CadUser cadUserFull = new CadUser();
        cadUserFull.setVisible(true);
        desktop.add(cadUserFull);
    }    
   
    private void menCadProdFull() {
        CadProd cadProdFull = new CadProd();
        cadProdFull.setVisible(true);
        desktop.add(cadProdFull);
    }   
        
