    private void inserirIcon() {        
            URL localImagen = getClass().getResource("/image/BackgroundIcon48x48.png");
            Image iconeTitulo = Toolkit.getDefaultToolkit().getImage(localImagen);
            this.setIconImage(iconeTitulo);        
    }
